import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  Cell,
  PieChart,
  Pie,
  Legend,
} from 'recharts';
import api from '../api';
import './StudentDashboard.css';

const PRIMARY = '#4f46e5';
const SKILL_COLORS = {
  'Memory Skills': '#6366f1',
  'Attention & Focus': '#0ea5e9',
  'Logical Reasoning': '#10b981',
  'Problem Solving': '#f59e0b',
  'Verbal Ability': '#ec4899',
  'Numerical Ability': '#8b5cf6',
  'Analytical Thinking': '#06b6d4',
  'Critical Thinking': '#ef4444',
  'Decision Making': '#84cc16',
  'Pattern Recognition': '#f97316',
};

export default function StudentDashboard() {
  const [stats, setStats] = useState(null);
  const [assessments, setAssessments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([
      api.get('/results/dashboard-stats'),
      api.get('/assessments'),
    ])
      .then(([statsRes, assessRes]) => {
        setStats(statsRes.data);
        setAssessments(assessRes.data.slice(0, 5));
      })
      .catch(() => setStats({ skillSummary: {}, trendData: [], recentResults: [] }))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="dashboard-loading">Loading dashboard...</div>;

  const skillSummary = stats?.skillSummary || {};
  const trendData = stats?.trendData || [];
  const recentResults = stats?.recentResults || [];

  const barData = Object.entries(skillSummary).map(([name, value]) => ({ name: name.length > 18 ? name.slice(0, 16) + '…' : name, fullName: name, value: value || 0 }));

  // Pie chart data: results count per subject
  const subjectDistribution = recentResults.length ? Object.entries(
    recentResults.reduce((acc, r) => {
      const name = r.subject?.name || 'Unknown';
      acc[name] = (acc[name] || 0) + 1;
      return acc;
    }, {})
  ).map(([name, value]) => ({ name, value })) : [];

  const upcoming = assessments[0];

  return (
    <div className="student-dashboard">
      <header className="welcome-header">
        <h1 className="dashboard-title">Welcome back!</h1>
        <p className="dashboard-subtitle">Here's your cognitive performance overview.</p>
      </header>

      <div className="stats-grid">
        <div className="stat-card glass-card color-1">
          <span className="stat-label">Average Score</span>
          <div className="stat-value">
            {recentResults.length
              ? Math.round(recentResults.reduce((acc, r) => acc + r.percentage, 0) / recentResults.length)
              : 0}%
          </div>
          <div className="stat-trend trend-up">
            ↑ 4.2% from last month
          </div>
        </div>
        <div className="stat-card glass-card color-2">
          <span className="stat-label">Tests Completed</span>
          <div className="stat-value">{recentResults.length}</div>
          <div className="stat-trend">
            Steady progress
          </div>
        </div>
        <div className="stat-card glass-card color-3">
          <span className="stat-label">Main Skill</span>
          <div className="stat-value" style={{ fontSize: '1.5rem', paddingTop: '10px' }}>
            {barData.length ? barData.sort((a, b) => b.value - a.value)[0].fullName : 'N/A'}
          </div>
        </div>
      </div>

      <div className="dashboard-charts">
        <section className="chart-card glass-card">
          <h2>Performance trend</h2>
          <div className="chart-wrap">
            {trendData.length ? (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={trendData} margin={{ top: 10, right: 10, left: -20, bottom: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="rgba(0,0,0,0.05)" />
                  <XAxis
                    dataKey="date"
                    axisLine={false}
                    tickLine={false}
                    tick={{ fontSize: 12, fill: '#94a3b8', fontWeight: 500 }}
                  />
                  <YAxis
                    domain={[0, 100]}
                    axisLine={false}
                    tickLine={false}
                    tick={{ fontSize: 12, fill: '#94a3b8', fontWeight: 500 }}
                  />
                  <Tooltip
                    contentStyle={{
                      borderRadius: '12px',
                      border: 'none',
                      boxShadow: '0 10px 15px -3px rgba(0,0,0,0.1)',
                      background: 'rgba(255, 255, 255, 0.95)',
                      backdropFilter: 'blur(4px)',
                      padding: '12px'
                    }}
                    itemStyle={{ color: PRIMARY, fontWeight: 700 }}
                    labelStyle={{ color: '#64748b', marginBottom: '4px', display: 'block' }}
                    formatter={(v) => [`${v}%`, 'Score']}
                  />
                  <Bar
                    dataKey="percentage"
                    fill={PRIMARY}
                    radius={[6, 6, 0, 0]}
                    barSize={32}
                  />
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="chart-empty">Complete assessments to see your trend.</div>
            )}
          </div>
        </section>

        <section className="chart-card glass-card">
          <h2>Skill Mastery</h2>
          <div className="chart-wrap">
            {barData.length && barData.some(d => d.value > 0) ? (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={barData} layout="vertical" margin={{ top: 0, right: 30, left: 40, bottom: 0 }}>
                  <XAxis type="number" hide />
                  <YAxis
                    type="category"
                    dataKey="name"
                    axisLine={false}
                    tickLine={false}
                    tick={{ fontSize: 12, fontWeight: 700, fill: '#64748b' }}
                  />
                  <Tooltip
                    cursor={{ fill: 'rgba(0,0,0,0.02)' }}
                    contentStyle={{ borderRadius: '16px', border: 'none', boxShadow: '0 20px 25px -5px rgba(0,0,0,0.1)', background: 'rgba(255, 255, 255, 0.9)', backdropFilter: 'blur(10px)' }}
                    formatter={(v) => [`${v}%`, 'Score']}
                  />
                  <Bar dataKey="value" radius={[0, 15, 15, 0]} barSize={24} fill={PRIMARY}>
                    {barData.map((entry, i) => (
                      <Cell key={entry.fullName} fill={PRIMARY} fillOpacity={0.8 - (i * 0.1)} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="chart-empty">Complete assessments to see skill mastery.</div>
            )}
          </div>
        </section>

        <section className="chart-card glass-card">
          <h2>Activity Distribution</h2>
          <div className="chart-wrap">
            {subjectDistribution.length ? (
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={subjectDistribution}
                    cx="50%"
                    cy="45%"
                    innerRadius={60}
                    outerRadius={85}
                    paddingAngle={5}
                    dataKey="value"
                  >
                    {subjectDistribution.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={Object.values(SKILL_COLORS)[index % 10]} />
                    ))}
                  </Pie>
                  <Tooltip
                    contentStyle={{ borderRadius: '12px', border: 'none', background: 'rgba(255,255,255,0.95)' }}
                  />
                  <Legend iconType="circle" wrapperStyle={{ fontSize: '11px', paddingTop: '20px' }} />
                </PieChart>
              </ResponsiveContainer>
            ) : (
              <div className="chart-empty">No activities to show.</div>
            )}
          </div>
        </section>
      </div>

      <section className="recent-results glass-card">
        <h2>Recent Performance</h2>
        {recentResults.length ? (
          <>
            <div className="results-table-header">
              <span>Subject</span>
              <span>Assessment</span>
              <span>Score</span>
              <span style={{ textAlign: 'right' }}>Date</span>
            </div>
            <ul className="results-list">
              {recentResults.map((r) => (
                <li key={r._id} className="result-item">
                  <span className="result-subject">
                    <span style={{ width: 8, height: 8, borderRadius: '50%', background: PRIMARY }}></span>
                    {r.subject?.name}
                  </span>
                  <span className="result-title">{r.assessment?.title}</span>
                  <span className="result-score">{r.percentage}%</span>
                  <span className="result-date">{new Date(r.completedAt).toLocaleDateString()}</span>
                </li>
              ))}
            </ul>
          </>
        ) : (
          <p className="no-results">No results yet. Take an assessment to see your scores here.</p>
        )}
      </section>
    </div>
  );
}
