import React, { useState, useEffect } from 'react';
import { Outlet, useNavigate, useLocation, NavLink } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import ProfileDropdown from './ProfileDropdown';
import './DashboardLayout.css';

export default function DashboardLayout() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(true);

  useEffect(() => {
    if (!user) return;
    if ((location.pathname === '/dashboard' || location.pathname === '/dashboard/') && user.role === 'admin') {
      navigate('/admin/subjects', { replace: true });
    } else if ((location.pathname === '/dashboard' || location.pathname === '/dashboard/') && user.role === 'teacher') {
      navigate('/teacher/questions', { replace: true });
    }
  }, [user, location.pathname, navigate]);

  const studentNav = [
    { to: '/dashboard', label: 'Home', icon: 'üè†', end: true },
    { to: '/dashboard/assessments', label: 'My Assessments', icon: 'üìù', end: false },
    { to: '/dashboard/reports', label: 'Reports', icon: 'üìä', end: false },
    { to: '/dashboard/profile', label: 'Profile', icon: 'üë§', end: false },
  ];

  const adminNav = [
    { to: '/admin/subjects', label: 'Subjects', icon: 'üìö', end: true },
    { to: '/admin/users', label: 'Users', icon: 'üë•', end: false },
  ];

  const teacherNav = [
    { to: '/teacher/questions', label: 'Questions', icon: '‚ùì', end: true },
    { to: '/teacher/students', label: 'Student', icon: 'üë®‚Äçüéì', end: false },
  ];

  const nav = user?.role === 'admin' ? adminNav : user?.role === 'teacher' ? teacherNav : studentNav;
  const base = user?.role === 'admin' ? '/admin/subjects' : user?.role === 'teacher' ? '/teacher/questions' : '/dashboard';

  return (
    <div className="dashboard-layout">
      {/* Background Glows */}
      <div className="glow-blob glow-1"></div>
      <div className="glow-blob glow-2"></div>
      <div className="glow-blob glow-3"></div>

      <aside className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-brand">
          <span className="sidebar-logo">‚óâ</span>
          <span className="sidebar-title">Cognitive Assessment</span>
        </div>
        <nav className="sidebar-nav">
          {nav.map(({ to, label, icon, end }) => (
            <NavLink
              key={to}
              to={to}
              end={end}
              className={({ isActive }) => `sidebar-link ${isActive ? 'active' : ''}`}
            >
              <span className="sidebar-icon">{icon}</span>
              <span className="sidebar-text">{label}</span>
            </NavLink>
          ))}
        </nav>
      </aside>
      <div className="main-wrap">
        <header className="dashboard-header">
          <button type="button" className="menu-btn" onClick={() => setSidebarOpen(!sidebarOpen)} aria-label="Toggle menu">
            ‚ò∞
          </button>
          <div className="header-spacer" />
          <ProfileDropdown />
        </header>
        <main className="dashboard-main">
          <Outlet />
        </main>
      </div>
    </div>
  );
}
