import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../api';
import './TakeTest.css';

export default function TakeTest() {
  const { assessmentId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [assessment, setAssessment] = useState(null);
  const [answers, setAnswers] = useState({});
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (user && user.role !== 'student') {
      setLoading(false);
      setError('Only students can take assessments.');
      return;
    }
    api.get(`/assessments/${assessmentId}/attempt`)
      .then(res => {
        setAssessment(res.data);
        setError('');
      })
      .catch(err => {
        setError(err.response?.status === 403 ? 'Only students can take assessments.' : 'Could not load assessment.');
        if (user?.role === 'student') navigate('/dashboard/assessments');
      })
      .finally(() => setLoading(false));
  }, [assessmentId, navigate, user]);

  const handleOptionSelect = (questionId, option) => {
    setAnswers(prev => ({ ...prev, [questionId]: option }));
  };

  const handleSubmit = async () => {
    if (!assessment) return;
    setSubmitting(true);
    try {
      await api.post('/results/submit', { assessmentId, answers });
      navigate('/dashboard/reports');
    } catch (err) {
      alert(err.response?.data?.message || 'Submit failed');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading && !error) return <div className="page-loading">Loading assessment...</div>;
  if (error && !assessment) return <div className="take-test take-test-error"><div className="card"><p>{error}</p><button type="button" className="btn btn-primary" onClick={() => navigate(user?.role === 'teacher' ? '/teacher/questions' : user?.role === 'admin' ? '/admin/subjects' : '/dashboard/assessments')}>Go back</button></div></div>;

  const questions = assessment?.questions || [];
  const current = questions[currentIndex];
  const options = current && Array.isArray(current.options) ? current.options : [];
  const answeredCount = Object.keys(answers).length;
  const allAnswered = questions.length > 0 && answeredCount === questions.length;

  return (
    <div className="take-test">
      <div className="test-header card">
        <h1>{assessment.title}</h1>
        <p>{assessment.subject?.name} • {questions.length} questions</p>
        <div className="test-progress">
          Question {currentIndex + 1} of {questions.length} • Answered: {answeredCount}
        </div>
      </div>

      <div className="test-body card">
        {current ? (
          <>
            <div className="question-block">
              <h2 className="question-text">{current.questionText}</h2>
              <p className="question-difficulty">{current.difficulty}</p>
              <ul className="options-list">
                {options.map((opt, i) => (
                  <li key={i}>
                    <label className="option-label">
                      <input
                        type="radio"
                        name={`q-${current._id}`}
                        checked={answers[current._id] === opt}
                        onChange={() => handleOptionSelect(current._id, opt)}
                      />
                      <span>{opt}</span>
                    </label>
                  </li>
                ))}
              </ul>
            </div>
            <div className="test-nav">
              <button
                type="button"
                className="btn btn-secondary"
                disabled={currentIndex === 0}
                onClick={() => setCurrentIndex(i => i - 1)}
              >
                Previous
              </button>
              {currentIndex < questions.length - 1 ? (
                <button type="button" className="btn btn-primary" onClick={() => setCurrentIndex(i => i + 1)}>
                  Next
                </button>
              ) : (
                <button
                  type="button"
                  className="btn btn-primary"
                  disabled={!allAnswered || submitting}
                  onClick={handleSubmit}
                >
                  {submitting ? 'Submitting...' : 'Submit'}
                </button>
              )}
            </div>
          </>
        ) : (
          <p>No questions in this assessment.</p>
        )}
      </div>

      <div className="test-quick-nav card">
        <p>Jump to question:</p>
        <div className="quick-dots">
          {questions.map((q, i) => (
            <button
              key={q._id}
              type="button"
              className={`quick-dot ${i === currentIndex ? 'active' : ''} ${answers[q._id] ? 'answered' : ''}`}
              onClick={() => setCurrentIndex(i)}
              title={`Question ${i + 1}`}
            >
              {i + 1}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
