import React, { useState, useEffect } from 'react';
import api from '../api';
import './AdminUsers.css'; // Reusing admin users layout/styles

export default function TeacherStudents() {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        api.get('/users?role=student')
            .then(res => setStudents(res.data))
            .catch(() => setStudents([]))
            .finally(() => setLoading(false));
    }, []);

    if (loading) return <div className="page-loading">Loading students...</div>;

    return (
        <div className="admin-users">
            <h1 className="page-title">Students</h1>
            <p className="page-desc">Overview of all registered students.</p>
            <div className="card">
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Details</th>
                        </tr>
                    </thead>
                    <tbody>
                        {students.map((s) => (
                            <tr key={s._id || s.id}>
                                <td><strong>{s.name}</strong></td>
                                <td>{s.email}</td>
                                <td>
                                    {s.rollNumber && `Roll: ${s.rollNumber}`}
                                    {s.course && ` • ${s.course}`}
                                    {s.department && ` • ${s.department}`}
                                    {!s.rollNumber && !s.course && !s.department && '—'}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
