import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../api';
import './MyAssessments.css';

export default function MyAssessments() {
  const [assessments, setAssessments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get('/assessments')
      .then(res => setAssessments(res.data))
      .catch(() => setAssessments([]))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="page-loading">Loading...</div>;

  return (
    <div className="my-assessments">
      <h1 className="page-title">My Assessments</h1>
      <p className="page-desc">Available tests by subject. Click Start to attempt.</p>
      <div className="assessments-grid">
        {assessments.length ? assessments.map((a) => (
          <div key={a._id} className="assessment-card card">
            <h3>{a.title}</h3>
            <p className="assessment-meta">{a.subject?.name}</p>
            <p className="assessment-questions">{a.questions?.length || 0} questions â€¢ {a.durationMinutes || 30} min</p>
            <Link to={`/dashboard/test/${a._id}`} className="btn btn-primary btn-sm">Start Test</Link>
          </div>
        )) : (
          <p className="no-data">No assessments available yet.</p>
        )}
      </div>
    </div>
  );
}
