import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  Cell,
} from 'recharts';
import api from '../api';
import './StudentDashboard.css';

const PRIMARY = '#2563eb';
const SKILL_COLORS = {
  'Memory Skills': '#6366f1',
  'Attention & Focus': '#0ea5e9',
  'Logical Reasoning': '#10b981',
  'Problem Solving': '#f59e0b',
  'Verbal Ability': '#ec4899',
  'Numerical Ability': '#8b5cf6',
  'Analytical Thinking': '#06b6d4',
  'Critical Thinking': '#ef4444',
  'Decision Making': '#84cc16',
  'Pattern Recognition': '#f97316',
};

export default function StudentDashboard() {
  const [stats, setStats] = useState(null);
  const [assessments, setAssessments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([
      api.get('/results/dashboard-stats'),
      api.get('/assessments'),
    ])
      .then(([statsRes, assessRes]) => {
        setStats(statsRes.data);
        setAssessments(assessRes.data.slice(0, 5));
      })
      .catch(() => setStats({ skillSummary: {}, trendData: [], recentResults: [] }))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="dashboard-loading">Loading dashboard...</div>;

  const skillSummary = stats?.skillSummary || {};
  const trendData = stats?.trendData || [];
  const recentResults = stats?.recentResults || [];

  const barData = Object.entries(skillSummary).map(([name, value]) => ({ name: name.length > 18 ? name.slice(0, 16) + '…' : name, fullName: name, value: value || 0 }));

  const upcoming = assessments[0];

  return (
    <div className="student-dashboard">
      <h1 className="dashboard-title">Dashboard</h1>


      <section className="take-assessment card">
        <h2>Take Assessment</h2>
        {upcoming ? (
          <div className="upcoming-test">
            <div>
              <strong>{upcoming.title}</strong>
              <p className="upcoming-meta">{upcoming.subject?.name} • {new Date(upcoming.scheduledAt).toLocaleDateString()}</p>
            </div>
            <Link to={`/dashboard/test/${upcoming._id}`} className="btn btn-primary">Start Test</Link>
          </div>
        ) : (
          <p className="no-upcoming">No upcoming test. Check My Assessments for available tests.</p>
        )}
      </section>

      <div className="dashboard-charts">
        <section className="chart-card card">
          <h2>Performance trend</h2>
          <div className="chart-wrap">
            {trendData.length ? (
              <ResponsiveContainer width="100%" height={260}>
                <LineChart data={trendData} margin={{ top: 5, right: 10, left: 0, bottom: 5 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e2e8f0" />
                  <XAxis dataKey="date" tick={{ fontSize: 11 }} />
                  <YAxis domain={[0, 100]} tick={{ fontSize: 11 }} />
                  <Tooltip formatter={(v) => [`${v}%`, 'Score']} />
                  <Line type="monotone" dataKey="percentage" stroke={PRIMARY} strokeWidth={2} dot={{ r: 3 }} />
                </LineChart>
              </ResponsiveContainer>
            ) : (
              <div className="chart-empty">Complete assessments to see your trend.</div>
            )}
          </div>
        </section>
        <section className="chart-card card">
          <h2>Skill-wise progress</h2>
          <div className="chart-wrap">
            {barData.length && barData.some(d => d.value > 0) ? (
              <ResponsiveContainer width="100%" height={260}>
                <BarChart data={barData} layout="vertical" margin={{ top: 5, right: 20, left: 60, bottom: 5 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e2e8f0" />
                  <XAxis type="number" domain={[0, 100]} tick={{ fontSize: 11 }} />
                  <YAxis type="category" dataKey="name" width={58} tick={{ fontSize: 10 }} />
                  <Tooltip formatter={(v) => [`${v}%`, 'Score']} />
                  <Bar dataKey="value" radius={[0, 4, 4, 0]}>
                    {barData.map((entry, i) => (
                      <Cell key={entry.fullName} fill={SKILL_COLORS[entry.fullName] || PRIMARY} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="chart-empty">Complete assessments to see skill-wise progress.</div>
            )}
          </div>
        </section>
      </div>

      <section className="recent-results card">
        <h2>Recent results</h2>
        {recentResults.length ? (
          <ul className="results-list">
            {recentResults.map((r) => (
              <li key={r._id} className="result-item">
                <span className="result-subject">{r.subject?.name}</span>
                <span className="result-title">{r.assessment?.title}</span>
                <span className="result-score">{r.percentage}%</span>
                <span className="result-date">{new Date(r.completedAt).toLocaleDateString()}</span>
              </li>
            ))}
          </ul>
        ) : (
          <p className="no-results">No results yet. Take an assessment to see your scores here.</p>
        )}
      </section>
    </div>
  );
}
